SELECT COUNT(*) AS TOTAL FROM( SELECT RI.OBJECT_ID,DIAG_LAST_SEQ,INST_CODE FROM ( SELECT RII.OBJECT_ID, DIAG_LAST_SEQ, CASE WHEN IFNULL(PS.OBJECT_ID,'') != '' THEN 'C' ELSE 'G' END INST_CODE, MTB.ABO FROM ( SELECT RII.OBJECT_ID, DIAG_LAST_SEQ FROM RD_INTEGRATION_INFO RII LEFT JOIN ( SELECT DESC1, DESC2, DESC3 FROM csbrain2.mb_etccd WHERE KDCD = 'D002' AND UNUSEYN='N' GROUP BY DESC1, DESC2) DIAG_NM_CD ON DIAG_NM_CD.DESC1 = RII.DIAG_NM1 WHERE 1=1
AND RII.OBJECT_ID IN (${objectId})
AND RII.PATIENT_SEX = #{patientSex}
AND RII.AGE >= #{startAge}
AND RII.AGE <= #{endAge}
AND RII.DIAG_LAST_SEQ IN (${diagLastSeq})
AND ${petResult}
AND ${genomeChipNm}
AND ${cdrStep}
AND IFNULL(APOE_CD,'') != ''
AND (${apoeCd})
AND IFNULL(EDU_LEVEL,'') != ''
AND (${eduLevel})
AND IFNULL(brainwave,'') != ''
AND IFNULL(brainwave,'') = ''
AND (
${schKeyWord}
OR
( ${diagNm1} )
OR
( ${diagNm2} )
)
AND NOT EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
AND EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
GROUP BY RII.OBJECT_ID, RII.NAME, RII.AGE, RII.PATIENT_SEX, RII.DIAG_LAST_SEQ, RII.GENOME_CHIP_NM, RII.APOE_CD ) RII LEFT JOIN RD_PILOT_STUDY PS ON RII.OBJECT_ID = PS.OBJECT_ID LEFT JOIN(SELECT OBJECT_IDX, GROUP_CONCAT(DISTINCT ABO ORDER BY testdate SEPARATOR' : ') ABO FROM csbrain2.mb_testblod WHERE IFNULL(ABO,'') != '' GROUP BY OBJECT_IDX)MTB ON RII.OBJECT_ID = MTB.OBJECT_IDX )RI WHERE 1=1
AND INST_CODE IN (${instCode})
AND IFNULL(ABO,'') != ''
AND (${abo})
) A

SELECT IFNULL(MAX(A.DIAG_LAST_SEQ),0) MAX_SEQ FROM( SELECT RI.OBJECT_ID,DIAG_LAST_SEQ,INST_CODE FROM ( SELECT RII.OBJECT_ID, DIAG_LAST_SEQ, CASE WHEN IFNULL(PS.OBJECT_ID,'') != '' THEN 'C' ELSE 'G' END INST_CODE, MTB.ABO FROM ( SELECT RII.OBJECT_ID, DIAG_LAST_SEQ FROM RD_INTEGRATION_INFO RII LEFT JOIN ( SELECT DESC1, DESC2, DESC3 FROM csbrain2.mb_etccd WHERE KDCD = 'D002' AND UNUSEYN='N' GROUP BY DESC1, DESC2) DIAG_NM_CD ON DIAG_NM_CD.DESC1 = RII.DIAG_NM1 WHERE 1=1
AND RII.OBJECT_ID IN (${objectId})
AND RII.PATIENT_SEX = #{patientSex}
AND RII.AGE >= #{startAge}
AND RII.AGE <= #{endAge}
AND RII.DIAG_LAST_SEQ IN (${diagLastSeq})
AND ${petResult}
AND ${genomeChipNm}
AND ${cdrStep}
AND IFNULL(APOE_CD,'') != ''
AND (${apoeCd})
AND IFNULL(EDU_LEVEL,'') != ''
