AND NOT EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
AND EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
GROUP BY RII.OBJECT_ID, RII.NAME, RII.AGE, RII.PATIENT_SEX, RII.DIAG_LAST_SEQ, RII.GENOME_CHIP_NM, RII.APOE_CD ) RII LEFT JOIN RD_PILOT_STUDY PS ON RII.OBJECT_ID = PS.OBJECT_ID LEFT JOIN(SELECT OBJECT_IDX, GROUP_CONCAT(DISTINCT ABO ORDER BY testdate SEPARATOR' : ') ABO FROM csbrain2.mb_testblod WHERE IFNULL(ABO,'') != '' GROUP BY OBJECT_IDX)MTB ON RII.OBJECT_ID = MTB.OBJECT_IDX )RI WHERE 1=1
AND INST_CODE IN (${instCode})
AND IFNULL(ABO,'') != ''
AND (${abo})
LIMIT #{start}, #{limit}
) A

SELECT RI.OBJECT_ID, CONCAT(INST_CODE,PATIENT_SEX,LPAD(RI.OBJECT_ID,5,'0')) SUBJECT_ID , NAME, AGE, PATIENT_SEX, DIAG_SEQ, DIAG_LAST_SEQ, DIAG_NM1, DIAG_NM2, DIAG_AGE, DIAG_DATE , DOCTOR_NAME, MMSE, SNSB, MRI, PET, BLOOD, CSF, BRAINWAVE, GENOME_CHIP_NM, PET_RESULT, PET_SUBJECT, PET_RESULT_V, CDR_STEP , DIAG_NM_BG_COLOR ,DIAG_NM_COLOR, ABO , APOE_CD , PET_SUVR , PET_AI_SUVR , EDU_LEVEL FROM ( SELECT RII.OBJECT_ID, NAME, AGE, PATIENT_SEX, DIAG_SEQ, DIAG_LAST_SEQ, DIAG_NM1, DIAG_NM2, DIAG_AGE, DIAG_DATE , CASE WHEN IFNULL(PS.OBJECT_ID,'') != '' THEN 'C' ELSE 'G' END INST_CODE , DOCTOR_NAME, MMSE, SNSB, MRI, PET, BLOOD, CSF, BRAINWAVE, GENOME_CHIP_NM, MR.PET_RESULT, MR.PET_SUBJECT, PET_RESULT_V, CDR_STEP , DIAG_NM_BG_COLOR , DIAG_NM_COLOR , APOE_CD , RII.ABO ABO , RII.PET_SUVR , RII.PET_AI_SUVR , RII.EDU_LEVEL FROM ( SELECT RII.OBJECT_ID, RII.NAME, RII.AGE, RII.PATIENT_SEX , GROUP_CONCAT(RII.DIAG_SEQ ORDER BY RII.DIAG_SEQ SEPARATOR',' ) DIAG_SEQ , DIAG_LAST_SEQ , GROUP_CONCAT(IFNULL(RII.DIAG_NM1,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',' ) DIAG_NM1 , GROUP_CONCAT(IFNULL(RII.DIAG_NM2,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',' ) DIAG_NM2 , GROUP_CONCAT(IFNULL(RII.DIAG_AGE,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',' ) DIAG_AGE , GROUP_CONCAT(IFNULL(RII.DIAG_DATE,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',' ) DIAG_DATE , GROUP_CONCAT(IFNULL(RII.DOCTOR_NAME,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') DOCTOR_NAME , GROUP_CONCAT(IFNULL(RII.MMSE,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') MMSE , GROUP_CONCAT(IFNULL(RII.SNSB,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') SNSB , IFNULL(RII.MRI,'') MRI , IFNULL(RII.PET,'') PET , IFNULL(RII.BLOOD,'') BLOOD , IFNULL(RII.CSF,'') CSF , IFNULL(RII.BRAINWAVE,'') BRAINWAVE , IFNULL(RII.PET_RESULT,'') PET_RESULT_V , GROUP_CONCAT(IFNULL(RII.CDR_STEP,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') CDR_STEP , IFNULL(RII.GENOME_CHIP_NM,'') GENOME_CHIP_NM , GROUP_CONCAT(IFNULL(DIAG_NM_CD.DESC2,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') DIAG_NM_BG_COLOR , GROUP_CONCAT(IFNULL(DIAG_NM_CD.DESC3,'') ORDER BY RII.DIAG_SEQ, DOCTOR_NAME, DIAG_DATE SEPARATOR',') DIAG_NM_COLOR , RII.APOE_CD , RII.ABO , RII.PET_SUVR , RII.PET_AI_SUVR
, RII.EDU_LEVEL FROM RD_INTEGRATION_INFO RII LEFT JOIN ( SELECT DESC1, DESC2, DESC3 FROM csbrain2.mb_etccd WHERE KDCD = 'D002' AND UNUSEYN='N' GROUP BY DESC1, DESC2) DIAG_NM_CD ON DIAG_NM_CD.DESC1 = RII.DIAG_NM1 WHERE 1=1
AND RII.OBJECT_ID IN (${objectId})
AND RII.PATIENT_SEX = #{patientSex}
AND RII.AGE >= #{startAge}
AND RII.AGE <= #{endAge}
AND RII.DIAG_LAST_SEQ IN (${diagLastSeq})
AND ${petResult}
AND ${genomeChipNm}
AND ${cdrStep}
AND IFNULL(APOE_CD,'') != ''
AND (${apoeCd})
AND IFNULL(EDU_LEVEL,'') != ''
AND (${eduLevel})
AND IFNULL(brainwave,'') != ''
AND IFNULL(brainwave,'') = ''
AND (
${schKeyWord}
OR
( ${diagNm1} )
OR
( ${diagNm2} )
)
AND NOT EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
AND EXISTS(SELECT 1 FROM RD_INTEGRATION_INFO B WHERE RII.IDX = B.IDX AND DIAG_LAST_SEQ = 1 AND DIAG_NM1 = '')
GROUP BY RII.OBJECT_ID, RII.NAME, RII.AGE, RII.PATIENT_SEX, RII.DIAG_LAST_SEQ, RII.GENOME_CHIP_NM, RII.APOE_CD, RII.EDU_LEVEL) RII LEFT JOIN (SELECT MR.OBJECT_IDX , GROUP_CONCAT(CASE MR.RCTU_01 WHEN '0' THEN 'Negative (-)' WHEN '0.5' THEN 'Negative (0.5)' WHEN '1' THEN 'Positive (+)' ELSE 'Error' END ORDER BY MR.SELECTION_NUM SEPARATOR' : ') PET_RESULT , GROUP_CONCAT((SELECT NAME FROM csbrain2.mb_subject ms WHERE IDX = SUBJECT_IDX) ORDER BY MR.SELECTION_NUM SEPARATOR' : ') PET_SUBJECT FROM csbrain2.mb_rctu MR WHERE FLAG ='U' GROUP BY MR.OBJECT_IDX) MR ON RII.OBJECT_ID = MR.OBJECT_IDX LEFT JOIN RD_PILOT_STUDY PS ON RII.OBJECT_ID = PS.OBJECT_ID )RI WHERE 1=1
AND INST_CODE IN (${instCode})
AND IFNULL(ABO,'') != ''
AND (${abo})
LIMIT #{start}, #{limit}

